import React from 'react';
import ReactDOM from 'react-dom';



const Header = (props) => {
  return (
    <div>
      <h1> {props.course} </h1>
    </div>
  )
}

const Content = (props) => {

  return (
    <div>

      <p>
        {props.part} {props.exercises}
      </p>
    </div>
  )
}

const Total = (props) => {
  return (
    <p> 
    Number of exercises {props.total} 
    </p>
  )
}


const App = () => { // arrow function
  const course_name = 'Half Stack application development'
  const course_content = {
    'part1' : {
      'name' : 'Fundamentals of React',
      'exersises' : 10
    },

    'part2' : {
      'name' : 'Using props to pass data',
      'exersises' : 7
    },

    'part3' : {
      'name' : 'State of a component',
      'exersises' : 14
    }
  }
  console.log(course_content)
  var total_exersises = 0
  for (let part in course_content) {
    console.log(part)
    total_exersises += part.exercises
  }


  return (
    <div>
      <Header course={course_name} />
      <Content course_content={course_content}/>
      <Total total={total_exersises} />  
    </div>
  )
}


ReactDOM.render(<App />, document.getElementById('root'))
