import React from 'react';
import ReactDOM from 'react-dom';



const Header = (props) => {
  return (
    <div>
      <h1> {props.course} </h1>
    </div>
  )
}

const Content = (props) => {
  return (
    <div>
      <p>
        {props.part} {props.exercises}
      </p>
    </div>
  )
}

const Total = (props) => {
  return (
    <p> 
    Number of exercises {props.total} </p>
  )
}


const App = () => { // arrow function
  const course = 'Half Stack application development'
  const course_parts = {
    'part1' : {
      'name' : 'Fundamentals of React',
      'excersises' : 10
    },

    'part2' : {
      'name' : 'Using props to pass data',
      'excersises' : 7
    },

    'part3' : {
      'name' : 'State of a component',
      'excersises' : 14
    }
  }


  const total_courses = exercises1 + exercises2 + exercises3

  return (
    <div>
      <Header course={course} />
      <Content />
      <Total total={total_excersises} />  
    </div>
  )
}


ReactDOM.render(<App />, document.getElementById('root'))
