import React from 'react';
import ReactDOM from 'react-dom';

const Part = (props) => {
  <p>
    {props.part} {props.exercises}
  </p>
}

const Header = (props) => {
  return (
    <div>
      <h1> {props.course} </h1>
    </div>
  )
}

const Content = (props) => {

  return (
    <div>
      <Part part={props.part1.name} excercises={props.part1.exercises} />
      <Part part={props.part2.name} excercises={props.part2.exercises} />
      <Part part={props.part3.name} excercises={props.part3.exercises} />
    </div>
  )
}

const Total = (props) => {
  return (
    <p> 
    Number of exercises {props.total} 
    </p>
  )
}


const App = () => { // arrow function
  const course_name = 'Half Stack application development'
  const course_content = {
    part1 : {
      name : 'Fundamentals of React',
      exercises : 10
    },

    part2 : {
      name : 'Using props to pass data',
      exercises : 7
    },

    part3 : {
      name : 'State of a component',
      exercises : 14
    }
  }

  var total_exercises = 0
  for (let part in course_content) {
    total_exercises += course_content[part].exercises
  }


  return (
    <div>
      <Header course={course_name} />
      <Content 
        part1={course_content.part1} 
        part2={course_content.part2}
        part3={course_content.part3}  
      />
      <Total total={total_exercises} />  
    </div>
  )
}


ReactDOM.render(<App />, document.getElementById('root'))
