import React from 'react';
import ReactDOM from 'react-dom';



const Header = (props) => {
  return (
    <div>
      <h1> {props.course} </h1>
    </div>
  )
}

const Content = (props) => {

  return (
    <div>

      <p>
        {props.part} {props.exercises}
      </p>
    </div>
  )
}

const Total = (props) => {
  return (
    <p> 
    Number of exercises {props.total} 
    </p>
  )
}


const App = () => { // arrow function
  const course_name = 'Half Stack application development'
  const course_content = {
    part1 : {
      name : 'Fundamentals of React',
      exercises : 10
    },

    part2 : {
      name : 'Using props to pass data',
      exercises : 7
    },

    part3 : {
      name : 'State of a component',
      exercises : 14
    }
  }
  console.log(course_content)
  var total_exercises = 0
  console.log(total_exercises)
  for (let part in course_content) {
    console.log(course_content.part.exercises)
    total_exercises += part.exercises
  }
  console.log(total_exercises)


  return (
    <div>
      <Header course={course_name} />
      <Content course_content={course_content}/>
      <Total total={total_exercises} />  
    </div>
  )
}


ReactDOM.render(<App />, document.getElementById('root'))
